INSERT INTO `oppr` (`id`, `pid`, `title`, `article_content`, `status`, `added_by`, `added_on`) VALUES
(3, 1, 'IPDF/WIPDF', '<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">IIT Madras offers post-doctoral opportunities to PhD graduates who wish to have a career in teaching and research. The opportunities are available in almost all academic departments of IIT Madras. The institute also offers post-doctoral fellowships to women PhD scholars with a break in career, as an endeavour to reconnect them with research.</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">The following are links to the application forms, required certificates and guidelines for post-doctoral opportunities.</span></span></span></span></span></p>\r\n\r\n<h3><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><strong><u>Institute Post-Doctoral Fellowship </u></strong></span></span></h3>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Guidelines for Institute Post Doctoral Fellowship at IIT Madras : </span></span></span><a href=\"https://www.iitm.ac.in/sites/default/files/Others/guidelines_for_institute_pdf.pdf\" style=\"color:#0563c1; text-decoration:underline\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Click Here</span></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Application Form for Institute Post Doctoral Fellowship at IITMadras :&nbsp; </span></span></span><a href=\"https://www.iitm.ac.in/sites/default/files/Others/application_format_for_institute_pdf.pdf\" style=\"color:#0563c1; text-decoration:underline\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Click Here</span></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">The applications should be sent through email to <strong>drresearch@iitm.ac.in</strong> </span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><u><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Post-Doctoral Fellowship for Women with Break in Career </span></span></u></strong></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">Guidelines : </span></span></strong><a href=\"https://www.iitm.ac.in/sites/default/files/Others/guidelines_for_post_doctoral_fellowship_for_women_with_break_in_career.pdf\" style=\"color:#0563c1; text-decoration:underline\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Click Here</span></span></a> </span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">Application Form :</span></span></span></strong> <a href=\"https://www.iitm.ac.in/sites/default/files/Others/application_format_for_post_doctoral_fellowship_for_women_with_break_in_career.pdf\" style=\"color:#0563c1; text-decoration:underline\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">Click Here</span></span></a></span></span></p>\r\n\r\n<p><span style=\"font-size:11pt\"><span style=\"font-family:Calibri,&quot;sans-serif&quot;\"><strong><u><span style=\"font-size:13.5pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\">General Instructions: </span></span></u></strong></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">The department will scrutinize the application of a candidate and if found suitable, the department</span></span> <span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">will constitute a Departmental Evaluation Committee (DEC) and conduct a seminar-based evaluation</span></span> <span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">of the candidate (DEC) at the department level (can be in-person or on skype / video conferencing).&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><br />\r\n<span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">After the seminar, if the DEC recommends, your application will be forwarded for a formal interview at the Deans office. The decision of DEC will be conveyed to you in a short span of time.</span></span></span></span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">This evaluation will be with respect to: </span></span><br />\r\n<span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">1. The strength and the novelty of the proposal. The proposal should not be a mere</span></span> <span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">extension of the applicants Ph.D. work.&nbsp;&nbsp;&nbsp;&nbsp; </span></span><br />\r\n<span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">2. The expertise brought in by the applicant to the department&rsquo;s / mentor&rsquo;s&nbsp;&nbsp; research area(s).</span></span><br />\r\n<span style=\"font-size:12.5pt\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\">3. Value addition to the department / applicant.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><br />\r\n<br />\r\n<span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">We envisage a time-line of around one&nbsp;month (from date of receipt of your application from the Deans office), to complete the DEC.&nbsp;</span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12pt\"><span style=\"font-family:&quot;Times New Roman&quot;,&quot;serif&quot;\"><span style=\"background-color:white\"><span style=\"font-family:&quot;Arial&quot;,&quot;sans-serif&quot;\"><span style=\"color:black\">In case of any issues, you could contact the Chair of the Department&nbsp;IPDF committee: Prof. Rajesh Narayanan <strong>(email:rnarayanan@iitm.ac.in</strong>).&nbsp;</span></span></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '1', 'admin@mail.com', '2022-06-16 11:36:03'),
(6, 1, 'National Postdoctoral fellowship', '<p>We encourage candidates to apply for the &quot;National Post Doctoral Fellowship (N-PDF)&quot; program to work in various research groups of the department carrying out cutting-edge research. Interested candidates may contact the faculty members directly.</p>\r\n\r\n<p>For more information, please check the link: <a href=\"https://serbonline.in/SERB/npdf\" target=\"_blank\">https://serbonline.in/SERB/npdf</a></p>\r\n', '1', 'admin@mail.com', '2022-08-27 12:11:01'),
(8, 1, 'Faculty Recruitment', '<p>The Physics department of IIT Madras is looking for exceptional candidates at the Assistant Professor level in various areas of physics.</p>\r\n\r\n<p>Please visit this space for recruitment notices for specific areas of research, which will be posted here from time to time. You are also advised to check the faculty recruitment portal of IIT Madras (<a href=\"https://facapp.iitm.ac.in/\" target=\"_blank\">https://facapp.iitm.ac.in/</a>). Application forms for all the faculty positions are filled online against the advertisement.</p>\r\n\r\n<p>If you are interested in visiting our department, kindly contact the faculty member of the department working in the area close to your research interest.</p>\r\n', '1', 'admin@mail.com', '2022-08-27 12:14:47');
